import React from 'react';
import {Link} from 'react-router-dom';
import {API_TOKEN_NAME} from '../constants';
import { scAxios } from '../';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const getProductDeatils = (id) => {
    return new Promise((resolve, reject) => {
        const req = scAxios.request('/products/productdetail/'+id, {
            method: 'get',
            headers: {
                'Accept': 'application/json',
                'Authorization': 'Bearer ' + localStorage.getItem(API_TOKEN_NAME)
            }
        });
        req.then(res => resolve(res.data))
            .catch(err => reject(err));
    });
}
class ViewProduct extends React.Component {
  state = {
        fields: {},
        errors: {},
        productlist:[],
        pid:'',
        ptitle:'',
        psku:'',
        pdesc:'',
        pamount:'',
  };
  refreshProductDeatils = (id) => {
    getProductDeatils(id)
    .then(res => {
      if(res.status===true){
          var records = res.data;
          this.setState({
            pid: records.id,
            ptitle: records.product_name,
            psku: records.product_sku,
            pdesc: records.product_desc,
            pamount: records.product_amount, 
          });
      } else {
         /*toast.error(res.message, {
            position: toast.POSITION.BOTTOM_RIGHT
          });*/
      }
    })
    .catch(err => {
        console.log(err);
    });
  }
  componentDidMount() {
    var id = this.props.match.params.id;
    console.log(id);
    this.refreshProductDeatils(id);
  }
  render(){
    return (
      <div>
        <div className="container">
          <h1>View Product</h1>
          <div className="row">
            <table>
              <tr>
                <th>Product Name:</th>
                <td>{this.state.ptitle}</td>
              </tr>
              <tr>
                <th>Product Sku:</th>
                <td>{this.state.psku}</td>
              </tr>
              <tr>
                <th>Product Desc:</th>
                <td>{this.state.pdesc}</td>
              </tr>
              <tr>
                <th>Product Amount:</th>
                <td>{this.state.pamount}</td>
              </tr>
            </table>
          </div>
        </div>
        <ToastContainer autoClose={5000} />
      </div>
    );
  }
}

export default ViewProduct;
