import React, {Component} from 'react';
import {API_TOKEN_NAME} from '../constants';
import { scAxios } from '../';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AddProduct = (products) => {
    return new Promise((resolve, reject) => {
       const req = scAxios.request('products/addproduct', {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Authorization': 'Bearer ' + localStorage.getItem(API_TOKEN_NAME)
            },
            data: {
                ...products
            }
        });
        req.then(res => resolve(res.data))
            .catch(err => reject(err));
    });
}
class CreateProduct extends Component {
	state = {
        fields: {},
        errors: {},
        pId:'',
        productTitle:'',
        productSku:'',
        productDesc:'',
        productAmount:'',
        pname:'',
        psku:'',
        pdesc:'',
        pamount:'',
        productlist:[],
    }
	handleChange = event => {
        this.setState({ errors:''});
        this.setState({
            [event.target.name]: event.target.value
        });
  }
  savedata = (e) => {
  	e.preventDefault();
  	const products = {
    		ptitle: this.state.productTitle,
    		psku: this.state.productSku,
        pdesc: this.state.productDesc,
        pamount: this.state.productAmount
  	}
		AddProduct(products)
		.then(res => {
        	if (res.status===true) {
            this.setState({
               blId: res.data.id,
               signup_success: true,
            });
            toast.success(res.message, {
                position: toast.POSITION.BOTTOM_RIGHT
            });
          } else {
              toast.error(res.message, {
                position: toast.POSITION.BOTTOM_RIGHT
              });
          }
      window.location.href = "/";    
		})
		.catch(err => {
    		console.log(err);
		});
	}
	render() {
      	return (
	      	<div>
            <div className="container">
  		        <h1>Create Product</h1>
  		        <form >
  		          <div className="row">
  		            <div className="col-md-6">
  		              <div className="form-group">
  		                <label>Product Name:</label>
  		                <input type="text" className="form-control" id="productTitle" name="productTitle" defaultValue={this.state.productTitle} onChange={this.handleChange}/>
  		              </div>
  		            </div>
  		          </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Product Sku:</label>
                      <input type="text" className="form-control" id="productSku" name="productSku" defaultValue={this.state.productSku} onChange={this.handleChange}/>
                    </div>
                  </div>
                </div>
  		          <div className="row">
  	              <div className="col-md-6">
  	                <div className="form-group">
  	                  <label>Product Description:</label>
  	                  <textarea className="form-control col-md-6" id="productDesc" name="productDesc" defaultValue={this.state.productDesc} onChange={this.handleChange}></textarea>
  	                </div>
  	              </div>
  		          </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Product Amount:</label>
                      <input type="text" className="form-control" id="productAmount" name="productAmount" defaultValue={this.state.productAmount} onChange={this.handleChange}/>
                    </div>
                  </div>
                </div>
                {/*<div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Product Title:</label>
                      <input type="file" className="form-control" id="productImg" name="productImg" defaultValue={this.state.productImg} onChange={this.handleChange}/>
                    </div>
                  </div>
                </div>*/}
                <br />
  		            <div className="form-group">
  		              <button className="btn btn-primary" onClick={this.savedata}>Add Product</button>
  		            </div>
  		        </form>
            </div>
            <ToastContainer autoClose={5000} />
	  		</div>
      	)
    }
}
export default CreateProduct;
