import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from "react-redux";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import * as userActions from './actions/userActions';
import ProductList from './component/Productlist.js';
import createProduct from './component/createProduct.js';
import EditProduct from './component/EditProduct.js';
import ViewProduct from './component/ViewProduct.js';
import './App.css';

class App extends Component {
  render() {
    return (
      <Router> 
        <Switch>
          <Route exact path="/" component={ProductList} />
          <Route path="/add-item" component={createProduct} />
          <Route path="/edit-product/:id" component={EditProduct} />
          <Route exact path="/view-product/:id" component={ViewProduct} />
        </Switch>
      </Router>
    );
  }
}

const mapStateToProps = (state /*, ownProps*/) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    actions: bindActionCreators(userActions, dispatch),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
