import React, {Component} from 'react';
import {API_TOKEN_NAME} from '../constants';
import { scAxios } from '../';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const UpdateProduct = (products, id) => {
    return new Promise((resolve, reject) => {
       const req = scAxios.request('/products/updateproduct/'+id, {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Authorization': 'Bearer ' + localStorage.getItem(API_TOKEN_NAME)
            },
            data: {
                ...products
            }
        });
        req.then(res => resolve(res.data))
            .catch(err => reject(err));
    });
}
const getProductDeatils = (id) => {
    return new Promise((resolve, reject) => {
        const req = scAxios.request('/products/productdetail/'+id, {
            method: 'get',
            headers: {
                'Accept': 'application/json',
                'Authorization': 'Bearer ' + localStorage.getItem(API_TOKEN_NAME)
            }
        });
        req.then(res => resolve(res.data))
            .catch(err => reject(err));
    });
}
class EditProduct extends Component {
	state = {
        fields: {},
        errors: {},
        pId:'',
        edit_productTitle:'',
        edit_productSku:'',
        edit_productDesc:'',
        edit_productAmount:'',
    }
	handleChange = event => {
        this.setState({ errors:''});
        this.setState({
            [event.target.name]: event.target.value
        });
  }
  updatedata = (e) => {
  	e.preventDefault();
    var id = this.state.pId;
  	const products = {
    		edit_ptitle: this.state.edit_productTitle,
    		edit_psku: this.state.edit_productSku,
        edit_pdesc: this.state.edit_productDesc,
        edit_pamount: this.state.edit_productAmount
  	}
		UpdateProduct(products, id)
		.then(res => {
        	if (res.status===true) {
            this.setState({
               pId: res.data.id,
            });
            toast.success(res.message, {
                position: toast.POSITION.BOTTOM_RIGHT
            });
          } else {
              toast.error(res.message, {
                position: toast.POSITION.BOTTOM_RIGHT
              });
          }
      window.location.href = "/";
		})
		.catch(err => {
    		console.log(err);
		});
	}
  refreshProductDeatils = (id) => {
    getProductDeatils(id)
    .then(res => {
      if(res.status===true){
            this.setState({
              pId: res.data.id,
              edit_productTitle: res.data.product_name,
              edit_productSku: res.data.product_sku,
              edit_productDesc: res.data.product_desc,
              edit_productAmount: res.data.product_amount,
            });
      } else {
         /*toast.error(res.message, {
            position: toast.POSITION.BOTTOM_RIGHT
          });*/
      }
    })
    .catch(err => {
        console.log(err);
    });
  }
  componentDidMount() {
    var id = this.props.match.params.id;
    this.refreshProductDeatils(id);
  }
	render() {
      	return (
	      	<div>
            <div className="container">
  		        <h1>Edit Product</h1>
  		        <form >
  		          <div className="row">
  		            <div className="col-md-6">
  		              <div className="form-group">
  		                <label>Product Name:</label>
  		                <input type="text" className="form-control" id="edit_productTitle" name="edit_productTitle" Value={this.state.edit_productTitle} onChange={this.handleChange}/>
  		              </div>
  		            </div>
  		          </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Product Sku:</label>
                      <input type="text" className="form-control" id="edit_productSku" name="edit_productSku" defaultValue={this.state.edit_productSku} onChange={this.handleChange}/>
                    </div>
                  </div>
                </div>
  		          <div className="row">
  	              <div className="col-md-6">
  	                <div className="form-group">
  	                  <label>Product Description:</label>
  	                  <textarea className="form-control col-md-6" id="edit_productDesc" name="edit_productDesc" defaultValue={this.state.edit_productDesc} onChange={this.handleChange}></textarea>
  	                </div>
  	              </div>
  		          </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Product Amount:</label>
                      <input type="text" className="form-control" id="edit_productAmount" name="edit_productAmount" defaultValue={this.state.edit_productAmount} onChange={this.handleChange}/>
                    </div>
                  </div>
                </div>
                {/*<div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label>Product Title:</label>
                      <input type="file" className="form-control" id="productImg" name="productImg" defaultValue={this.state.productImg} onChange={this.handleChange}/>
                    </div>
                  </div>
                </div>*/}
                <br />
  		            <div className="form-group">
  		              <button className="btn btn-primary" onClick={this.updatedata}>Update Product</button>
  		            </div>
  		        </form>
            </div>
            <ToastContainer autoClose={5000} />
	  		</div>
      	)
    }
}
export default EditProduct;
