import React from 'react';
import {Link} from 'react-router-dom';
import {API_TOKEN_NAME} from '../constants';
import { scAxios } from '../';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const getAllProducts = () => {
    return new Promise((resolve, reject) => {
        const req = scAxios.request('products/getallproducts', {
            method: 'get',
            headers: {
                'Accept': 'application/json',
                'Authorization': 'Bearer ' + localStorage.getItem(API_TOKEN_NAME)
            }
        });
        req.then(res => resolve(res.data))
            .catch(err => reject(err));
    });
}
const deleteProduct = (data) => {

  return new Promise((resolve, reject) => {
      const req = scAxios.request('/products/delete/'+data.id, {
          method: 'post',
          headers: {
              'Accept': 'application/json',
              'Authorization': 'Bearer ' + localStorage.getItem(API_TOKEN_NAME)
          }

      });

      req.then(res => resolve(res.data))
          .catch(err => reject(err));
  });
}
class Productlist extends React.Component {
  state = {
        fields: {},
        errors: {},
        productlist:[],
  };
  delete(id){
  const data = {
          id: id
      }

    deleteProduct(data)
      .then(res => {
        if(res.status==='1'){
          toast.success(res.message, {
            position: toast.POSITION.BOTTOM_RIGHT
          });
        }  else {
          toast.error(res.message, {
            position: toast.POSITION.BOTTOM_RIGHT
          });
        }
        this.componentDidMount();
      })
      .catch(err => {
          console.log(err);
      });
  }
  componentDidMount() {
    getAllProducts()
      .then(res => {
        if(res.status===true){
          var records = res.data;
          this.setState({ productlist: records });
        } else {
          toast.error(res.message, {
              position: toast.POSITION.BOTTOM_RIGHT
          });
        }
      })
      .catch(err => {
          console.log(err);
      });
  }
  render(){
    return (
      <div>
        <div className="container">
          <h1>Products</h1>
          <div className="row">
            <div className="col-md-10"></div>
            <div className="col-md-2">
              <Link to="/add-item" className="btn btn-primary">Create Product</Link>
            </div>
            <table className="table table-hover">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Product Name</th>
                    <th>Product Sku</th>
                    <th>Product Desc</th>
                    <th>Product Amount</th>
                    <th width="200px">Actions</th>
                </tr>
                </thead>
                <tbody>
                {
                  this.state.productlist.length > 0
                  ?
                  this.state.productlist.map(productdata => {
                  return(
                    <tr key={productdata.id}>
                      <td>{productdata.id}</td> 
                      <td>{productdata.product_name}</td>
                      <td>{productdata.product_sku}</td>
                      <td>{productdata.product_desc}</td>
                      <td>{productdata.product_amount}</td>
                      <td>
                        <Link to={`/edit-product/${productdata.id}`} className="btn btn-primary">Edit Product</Link>
                        <Link to={`/view-product/${productdata.id}`} className="btn btn-primary">View Product</Link>
                        <a href="#" className="tableicon btn btn-danger" onClick={(e) => { if (window.confirm('Are you sure you wish to delete this list ?')) this.delete(productdata.id)} }>Delete</a>
                      </td>
                    </tr>
                  )
                })
                :
                  <tr> <td>No result Found</td></tr>
                }

                </tbody>
            </table>
          </div>
        </div>
        <ToastContainer autoClose={5000} />
      </div>
    );
  }
}

export default Productlist;
